stage.addEventListener(KeyboardEvent.KEY_DOWN, fl_KeyboardDownHandler_2);

function fl_KeyboardDownHandler_2(event:KeyboardEvent):void
{
	switch (event.keyCode)
	{
		case 49:shoot1f(null);break; //выстрелы
		case 50:shoot2f(null);break;
		case 51:shoot3f(null);break;
		case 52:shoot4f(null);break;
		case 53:shoot5f(null);break;
		case 54:shoot6f(null);break;
		case 55:shoot7f(null);break;
		case 56:shoot8f(null);break;
		case 57:shoot9f(null);break;
		case 81:fl_MouseClickHandler_8(null);break; //Q
		case 87:fl_MouseClickHandler_12(null);break; //W
		case 69:evoc(null);break; //E
		case 82:healf(null);break; //R
		case 84:fl_MouseClickHandler(null);break;
		case 89:fl_MouseClickHandler_5(null);break;
		case 85:fl_MouseClickHandler_13(null);break;
		//case 73:break;
		//case 79:break;
		//case 80:break;
		default:trace(event.keyCode);break;

	}
}

