	timeex.text="Time "+String(timer/100);
	if (invinsibilitytimer>-1) bubblepicex.visible=true; else bubblepicex.visible=false;
	 if (invinsibilitytimer>-1) 
	 {
		 invtimerex.visible=true;
		 invtimerex.text=String(invinsibilitytimer/100);
	 } 
	 else invtimerex.visible=false;
	 if (windeffect>0) windeffectex.visible=true; else windeffectex.visible=false;
	if ((armeffect<150)&&(armeffect>0)) 
	   {
		   armeffectex.visible=true; 
		   for (var j:int=0;j<30;j++) sp1[j].destruct();
	   }
	 else armeffectex.visible=false;
	//отображение иконок абилок
	 if (shoota[0].readycheck()==1) shoot1.visible=true; else shoot1.visible=false;
	 if (shoota[1].readycheck()==1) shoot2.visible=true; else shoot2.visible=false;
	 if (shoota[2].readycheck()==1) shoot3.visible=true; else shoot3.visible=false;
	 if (shoota[3].readycheck()==1) shoot4.visible=true; else shoot4.visible=false;
	 if (shoota[4].readycheck()==1) shoot5.visible=true; else shoot5.visible=false;
	 if (shoota[5].readycheck()==1) shoot6.visible=true; else shoot6.visible=false;
	 if (shoota[6].readycheck()==1) shoot7.visible=true; else shoot7.visible=false;
	 if (shoota[7].readycheck()==1) shoot8.visible=true; else shoot8.visible=false;
	 if (shoota[8].readycheck()==1) shoot9.visible=true; else shoot9.visible=false;
	 if ((enresta.readycheck()==1)&&(enresta.getEnableLvl(level)==1)) evocex.visible=true; else evocex.visible=false;
	 if ((heala.readycheck()==1)&&(heala.getEnableLvl(level)==1)) renewex.visible=true; else renewex.visible=false;
	 if ((multishoota.readycheck()==1)&&(multishoota.getEnableLvl(level)==1)) multishootex.visible=true; else multishootex.visible=false;
	 if ((bubblea.readycheck()==1)&&(bubblea.getEnableLvl(level)==1)) bubbleex.visible=true; else bubbleex.visible=false;
