	 if ((arma.readycheck()==1)&&(arma.getEnableLvl(level)==1)) armex.visible=true; else armex.visible=false;
}


function levelup():void
{
		level++;
		switch(level)
	      {
		  case 1:
		     switch(mode)
			 {
				 case 1:leveluptext.text="Вы выбрали легкий путь.\nВыберите первое улучшение чтобы начать игру.";break;
				 case 2:leveluptext.text="Вы выбрали нормальный режим игры.\nВыберите первое улучшение чтобы начать игру.";break;
				 case 3:leveluptext.text="Вы выбрали героическую сложность игры.\nВыберите первое улучшение чтобы начать игру. Удачи!";break;
			 }break;
		  case 4:leveluptext.text="Поздравляем!\nВы достигли "+String(level)+"-го уровня\nДоступна новая способность \"Вьюга\"\nИспользуйте ее, чтобы замедлить пауков";break;
		  case 8:leveluptext.text="Поздравляем!\nВы достигли "+String(level)+"-го уровня\nДоступна новая способность \"Подготовка\"\nИспользуйте ее, чтобы восстановить часть энергии";break;
		  case 12:leveluptext.text="Поздравляем!\nВы достигли "+String(level)+"-го уровня\nДоступна новая способность \"Исцеление\"\nИспользуйте ее, чтобы восстановить часть здоровья";break;
		  case 15:leveluptext.text="Поздравляем!\nВы достигли "+String(level)+"-го уровня\nДоступна новая способность \"Залп\"\nИспользуйте ее, если появилось много пауков на разных линиях";break;
		  case 20:leveluptext.text="Поздравляем!\nВы достигли "+String(level)+"-го уровня\nДоступна новая способность \"Ни шагу назад!\"\nИспользуйте ее и вы станете временно неуязвимы";break;
		  case 30:leveluptext.text="Поздравляем!\nВы достигли "+String(level)+"-го уровня\nДоступна новая способность \"Армагеддон\"\nИспользовав ее вы уничтожите всех пауков на поле боя";break;
		  case 70:pauseen=3;fl_SC_2.stop();gotoAndStop(6);break;
		  default:leveluptext.text="Поздравляем!\nВы достигли "+String(level)+"-го уровня";break;
	      }

		timer=1900+level*200;
		talentpoints++;
		log("Поздравляем! Вы получили "+String(level)+" уровень");
		if (level!=1) playsound(5);
		pauseen=2;

}


function generate():void
{
for (i=0;i<30;i++) 
	if (sp1[i].getEnabled()==0)
	  {
	    sp1[i].generate(level);
		if (windeffect>0) sp1[i].Slow(75);
		return;
	  }
}


function log(s:String):void
{
	combatlog1.text=combatlog2.text;
	combatlog2.text=combatlog3.text;
	combatlog3.text=combatlog4.text;
	combatlog4.text=combatlog5.text;
	combatlog5.text=s;
}



function destroy(a:strela,b:spider)
{
	if (hp1>0) playsound(4);
	a.destruct();
	b.destruct();
}


function damage(a:int,b:int,type:int):void
{
	var dmg:int;
	dmg=a+Math.random()*(b-a);
	if (type==1) 
	  {
		if (invinsibilitytimer<0)
		{
		  hp1=hp1-dmg*(1-PWSt.getTeck()*0.08);
		  log("Паук наносит лучникам "+String(dmg)+" урона");
		  if (hp1>500) playsound(14);
		  else if (hp1>150) playsound(12);
		  else if (hp1>0) playsound(11);
		}
		else 
		{
			log("Паук пытается нанести вам урон, но вы неуязвимы");
			playsound(17);
		}

	  }
	else 
	{
		hp1=hp1+dmg;
		log("Лучники исцеляются на "+String(dmg)+" здоровья");
	}
	if (hp1>hpmax) hp1=hpmax;
	if (hp1<1) 
	{
		pauseen=3;
		fl_SC_2.stop();
		gotoAndStop(5);
	}
}

function showdatat(a:talent):void{
	tooltipname.text=a.getName();
	tooltip.text=String(a.getTeck())+"/"+String(a.getMax())+"\n"+a.getMark();
}

function showdataa(a:ability):void{
	tooltipname.text=a.getName();
	tooltip.text="Требуется "+String(a.getCost()/100)+" энергии\n"+String(a.getCd()/100)+" секунд перезарядка\n"+a.getMark();
}

function showdata(s:String,str:String):void{
	tooltipname.text=s;
	tooltip.text=str;
}


function cleardata():void{
	tooltipname.text="";
	tooltip.text="";
}


