  talentpoints-=agilityt.upgrade("Увеличивает максимальный запас энергии на "+String((agilityt.getTeck()+1)*120)+" ед.\n"+s);
  energymax=10000+agilityt.getTeck()*12000;
  log("Улучшено: Ловкость(Уровень "+String(agilityt.getTeck())+")");
  talentspend++;
  pauseen=0;
  }
else playsound(3);
showdatat(agilityt);
}



imprenewex.addEventListener(MouseEvent.CLICK,imprenew);
function imprenew(Event:MouseEvent):void {
	var s:String="";
if (imphealt.getTeck()<imphealt.getMax()-1) s="Следующий уровень - При использовании Исцеления дополнительно восстанавливается "+String((imphealt.getTeck()+2)*150)+" здоровья";
if ((talentpoints>0)&&(imphealt.getTeck()!=imphealt.getMax()))
  {
  talentpoints-=imphealt.upgrade("При использовании Исцеления дополнительно восстанавливается "+String((imphealt.getTeck()+1)*150)+" здоровья\n"+s);
  heala.update(1000,10000,"Восстанавливает вашим лучникам "+String(150+imphealt.getTeck()*150)+" здоровья");
  log("Улучшено: Усиленное исцеление(Уровень "+String(imphealt.getTeck())+")");
  talentspend++;
  pauseen=0;
  }
else playsound(3);
showdatat(imphealt);
}



